import React, { Fragment } from "react";
import { useState, useEffect } from "react";
import {useDispatch, useSelector} from "react-redux";  //hook
import {getpok, orderByName, volver  } from "../../redux/actions";
import {Link} from "react-router-dom";
import Tarjeta from "../tarjeta";
import Paginado from "../paginado";
import Buscador from "../buscador";
import style from "./Home.module.css";
    

export default function Home (){
    const dispatch = useDispatch()
    const pokemon = useSelector ((state) => state.pokemon); 
    

const [input, setInput] = useState ({
    nombre: "",
    imagen: "",
    description: "",    
})  


    const [error, setError] = useState ("")
    const [orden, setOrden] = useState ("") 
    const [currentPage, setCurrentPage] = useState (1)
    const [pokPerPage, setPokPerPage ] = useState(6)

    const indiceUltimoP = currentPage * pokPerPage //12
    const indicePrimerP = indiceUltimoP - pokPerPage //0
    const currentPok = pokemon?.slice(indicePrimerP, indiceUltimoP)// slice me trae una copia en la que eloriginal n se destruye
    const paginado = (pageNumber) =>{
        setCurrentPage (pageNumber)
    }


    useEffect(()=>{
        dispatch (getpok());
        setCurrentPage (1);
    },[dispatch])

    
    function handleClick (e){
        e.preventDefault();
        dispatch(volver());
        setCurrentPage (1);
        
    }


    function handleSort(e){
        e.preventDefault();
        dispatch (orderByName(e.target.value))
        setCurrentPage (1);
        setOrden (`Ordenado ${e.target.value}`)
    }
    

    return (
        <div >
            <div>
            
            <h2 className ={style.titulo}> LarnU <Buscador setCurrentPage = {setCurrentPage} /> </h2>
            
            </div>
          
       
            <div className ={style.contenedorBotones}> 
           
           <select className ={style.boton}  onChange={e =>handleSort(e)}>   
                <option value="asc">Ordenar A-Z </option>
                <option value="des">Ordenar Z-A </option>
            </select>
            

            <Link to = "/types"><button className ={style.boton}>Crea Curso </button></Link>
            
            <button className ={style.volver}  onClick={e=> {handleClick(e)}} >
                Volver a cargar 
             </button> 
             
            
            </div>
                   
        
         <div className = {style.container}>  
           {currentPok.length>0? currentPok.map ((p) =>{ 
            return(
                <Fragment >                    
                     <Link  to = {"/" + p.id}> 
                        <Tarjeta 
                        nombre ={p.name.charAt(0).toUpperCase() + p.name.slice(1) }  
                        imagen ={p.image}    
                        description ={p.description}   
                        key={p.id}/>
                    </Link>  
                </Fragment>
            ) 
        }) : <img className ={style.gif} src = "https://static.wixstatic.com/media/4023e9_de0d1a0ad7324cecbd3b3849ae8a75d6~mv2.gif"></img> 

        } 
        
        
         
        </div>  
    
        <div> 
                <Paginado
               
                pokPerPage={pokPerPage}
                pokemon ={pokemon.length}
                paginado ={paginado}
                currentPage ={currentPage}
                
                />
             
             </div>  

        </div> 
    
    ) //CIERRA EL RETURN
}
