import axios from "axios";

//detalle
export function getDetalle (id){
    return async function (dispatch) {
        try{
            var json = await axios.get ("http://localhost:3001/curso/" + id);
            return dispatch ({
                type: "GET_DETALLE",
                payload: json.data
            })
        }catch(error)  {
            console.log (error)
    } 
    }
}

export function deleteDetalle (){
    return{
        type: "DELETE",
}
}
